import React, { useState } from 'react';

const FilterPanel = ({ onFilterChange }) => {
    const [filters, setFilters] = useState({
        make: '',
        model: '',
        fuelType: '',
        transmission: '',
        year: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFilters({
            ...filters,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onFilterChange(filters);
    };

    return (
        <form onSubmit={handleSubmit} className="filter-panel">
            <div>
                <label>Make:</label>
                <input type="text" name="make" value={filters.make} onChange={handleChange} />
            </div>
            <div>
                <label>Model:</label>
                <input type="text" name="model" value={filters.model} onChange={handleChange} />
            </div>
            <div>
                <label>Fuel Type:</label>
                <select name="fuelType" value={filters.fuelType} onChange={handleChange}>
                    <option value="">All</option>
                    <option value="Petrol">Petrol</option>
                    <option value="Diesel">Diesel</option>
                    <option value="Electric">Electric</option>
            </select>
            </div>
            <div>
                <label>Transmission:</label>
                <select name="transmission" value={filters.transmission} onChange={handleChange}>
                    <option value="">All</option>
                    <option value="Manual">Manual</option>
                    <option value="Automatic">Automatic</option>
                </select>
            </div>
            <div>
                <label>Year:</label>
                <input type="number" name="year" value={filters.year} onChange={handleChange} />
            </div>
            <button type="submit">Apply Filters</button>
        </form>
    );
};

export default FilterPanel;
