import React from 'react';

const DataTable = ({ data, title, onComparisonToggle }) => {
    return (
        <div className="datatable">
            <h2>{title}</h2>
            <table>
                <thead>
                    <tr>
                        <th>Make</th>
                        <th>Model</th>
                        <th>Combined Consumption</th>
                        <th>CO2 Emissions</th>
                        <th>Compare</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item, index) => (
                        <tr key={index}>
                            <td>{item.make}</td>
                            <td>{item.model}</td>
                            <td>{item.combined_consumption}</td>
                            <td>{item.co2_emissions}</td>
                            <td>
                                <input 
                                    type="checkbox" 
                                    onChange={() => onComparisonToggle(item)} 
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default DataTable;
