import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';

const Chart = ({ data, title, type }) => {
  const [selectedItem, setSelectedItem] = useState(null);

  const chartData = {
    labels: data.map(item => item.make),
    datasets: [
      {
        label: 'Average Consumption',
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(75,192,192,0.4)',
        hoverBorderColor: 'rgba(75,192,192,1)',
        data: data.map(item => item.avg_cons)
      }
    ]
  };

  const ChartComponent = type === 'pie' ? Pie : Bar;

  const options = {
    scales: {
      y: {
        beginAtZero: true
      }
    },
    onClick: (event, elements) => {
      if (elements.length > 0) {
        const index = elements[0].index;
        alert(`You clicked on ${data[index].make}`);
      }
    }
  };

  return (
    <div className="chart">
      <h2>Average Consumption by Make</h2>
      <Bar data={chartData} />
    </div>,
    
    <div className="chart">
      <h2>{title}</h2>
      <Bar data={chartData} options={options} />
        {selectedItem && (
          <div className="detail-view">
            {/* Display details of selectedItem */}
          </div>
        )}
    </div>
        
  );
};

export default Chart;
